<script type="7f68c7e5cf399bdea990487a-text/javascript">
	var Strings = {
		create : (function() {
			var regexp = /{([^{]+)}/g;
			return function(str, o) {
				return str.replace(regexp, function(ignore, key){
					return (key = o[key]) == null ? '' : key;
				});
			}
		})()
	};
	function loadShopProfileRatings() {
		var restaurant_id = 132624;
		var ratingsOffset = $('#pills-ratings .ratigns-tab-ratings-list-item').length;
		var ratingsLimit = 20;
		var is_chain = true;
        var ratings = [];

		var api = 'https://api.e-food.gr/';
        var _url = api + 'api/v1/restaurants/' + restaurant_id + '/ratings/?limit=' + ratingsLimit + '&offset=' + ratingsOffset + '&mode=extended&comment_only=' + $('#reviews_without_comments, #mobile_reviews_without_comments').is(':checked')
        if (is_chain) _url += '&chain_mode=true';
		$.ajax({
			url: _url,
			contentType: 'application/json',
			async: false,
            headers: {
                "X-core-session-id": app.userSid,
                "Accept-Language": app.apiLocale,
                "X-core-platform": 'web',
                "X-core-version": '3',
            },
			success: function(response){
				if (response.error_code !== 'success'){
					return;
				}
				ratings = response.data.ratings || [];
				ratings_summary = response.data.summary;

				if (ratings.length == 0 && ratingsOffset == 0) {
					$('#pills-ratings--empty').removeClass('d-none');
				}
				if (ratings.length < ratingsLimit) {
					$('.shop-profile-more-ratings').hide();
				}
			}
		});

		var tpl = $('#rating_tpl').html();
		var html = '';
		ratings.forEach(function(rating){
			that = this;
			var m = {
				user_name: rating.first_name,
				initials: rating.first_name.charAt(0),
				quality: rating.quality,
				service: rating.service,
				delivery_time: rating.delivery_time,
				overall: function() {
                    return (rating.overall_numeric.toFixed(1));
                }(),
				created: rating.created,
				comment: rating.comment,
				restaurant_name: rating.restaurant.name,
				has_comment: ''
			}
			html += Strings.create(tpl, m);
        })
		$('#pills-ratings .ratigns-tab-ratings-list').append(html);
		$('.ratigns-tab-ratings--avg h3').text(ratings_summary.average.toFixed(1));
		$('.ratings-tab--quality p').text(ratings_summary.avg_quality.toFixed(1));
		$('.ratings-tab--quality .shop-ratings').html(efoodLib.renderRing());
        efoodLib.fillRings('.ratings-tab--quality', ratings_summary.avg_quality.toFixed(1));
		$('.ratings-tab--service p').text(ratings_summary.avg_service.toFixed(1));
		$('.ratings-tab--service .shop-ratings').html(efoodLib.renderRing());
        efoodLib.fillRings('.ratings-tab--service', ratings_summary.avg_service.toFixed(1));

        if ($('.ratings-tab--delivery-time').length) {
            $('.ratings-tab--delivery-time p').text(ratings_summary.avg_delivery_time.toFixed(1));
            $('.ratings-tab--delivery-time .shop-ratings').html(efoodLib.renderRing());
            efoodLib.fillRings('.ratings-tab--delivery-time', ratings_summary.avg_delivery_time.toFixed(1));
        }

	}

	$('a[href="#pills-ratings"]').on('show.bs.tab', function (e) {
		if ($('#pills-ratings .ratigns-tab-ratings-list li').length == 0) {
			loadShopProfileRatings();
		}
	});
	$('#reviews_without_comments, #mobile_reviews_without_comments').on('click', function(e){
		$('#reviews_without_comments').prop('checked',$(this).prop('checked'));
		$('#mobile_reviews_without_comments').prop('checked',$(this).prop('checked'));
		$('ul > .ratigns-tab-ratings-list-item').remove();
		$('#pills-ratings--empty').addClass('d-none');
