            var Strings = {
                create : (function() {
                    var regexp = /{([^{]+)}/g;
                    return function(str, o) {
                        return str.replace(regexp, function(ignore, key){
                            return (key = o[key]) == null ? '' : key;
                        });
                    }
                })()
            };
            function loadShopProfileOrders(_limit, _offset) {
                var restaurant_id = 132624;
                var currency_symbol = '€';
                var ordersOffset = _offset || $('#pills-my-orders ul li').length;
                var ordersLimit = _limit || 20;
                var orders = [];
                var api = 'https://api.e-food.gr/';
                $.ajax({
                    url: api + 'api/v1/user/orders/history?limit=' + ordersLimit + '&offset=' + ordersOffset + '&restaurant_id=' + restaurant_id,
                    headers: {
                        "X-core-session-id": '6bb82e9349e78b64654632375c318ce4',
                        "Accept-Language": app.apiLocale
                    },
                    contentType: 'application/json',
                    async: false,
                    success: function(response){
                        if (response.error_code !== 'success'){
                            return;
                        }
                        if (response.data.length == 0) {
                            $('#pills-my-orders-tab--empty').removeClass('d-none');
                        }
                        orders = response.data;
                        if (orders.length < ordersLimit) {
                            $('.shop-profile-more-orders').hide();
                        }
                    }
                });

                var tpl = $('#order_tpl').html();
                var html = '';
                orders.forEach(function(order){
                    that = this;
                    var overall_numeric = 0;
                    if (typeof order.rating.overall_numeric !== 'undefined')
                        overall_numeric = order.rating.overall_numeric.toFixed(1);

                    var m = {
                        order_id: order.id,

                        is_rated : order.is_rated,
                        has_ratings: order.is_rated? '': 'd-none',
                        hasnt_ratings: order.is_rated? 'd-none': '',
                        overall_numeric: overall_numeric,
                        can_be_rated : order.can_be_rated ? '' : 'd-none',

                        rating : order.rating,
                        // payment: (order.payment_type != 'credit_card') ? 'd-none' : '',
                        submission_date: order.submission_date,
                        shop_id: order.restaurant.id,
                        price: function() {
                            return  order.price.toLocaleString(app.locale, {minimumFractionDigits:2}) + currency_symbol;
                        }(),
                        products: function(){
                            var products = '';

                            order.products.some(function(product){
                                if (product.name !== '') {
                                    products += that.Strings.create('<div>{q} x {n}</div>', {q:product.quantity, n:product.name});
                                }
                            });

                            return products;
                        }(),
                        bags: function () {
                            var bags = '';

                            if (order.bags && order.bags.constructor === Object && order.bags.amount && order.bags.amount > 0) {
                                bags = that.Strings.create('<div>{q} x {n}</div>', { q: order.bags.count, n: 'Σακούλα' });
                            }

                            return bags;
                        }(),
                        csr: function () {
                            var csr = '';

                            if (order.csr.constructor === Object && Object.keys(order.csr).length > 0) {
                                csr = '<i class="fas fa-hand-holding-heart"></i> ' + '<span>' + order.csr.title + '</span> ';
                            }

                            return csr;
                        }(),
                        rider_tip: function () {
                            var tip = '';

                            if (order.tip) {
                                tip = '<div><i class="far fa-money-bill"></i><span class="ml-2">Tip για διανομέα</span></div>';
                            }

                            return tip;
                        }(),
                    }
                    html += Strings.create(tpl, m);
                })
                $('#pills-my-orders ul').append(html);
                $('#pills-my-orders').off('click', '.order-reorder')
                $('#pills-my-orders').on('click', '.order-reorder',function(){
                    var data = $(this).data();
                    if (typeof data.order_id !== 'undefined') {
                        window.ComponentCart.mountReorderModal(data.order_id);
                    }
                });
            }

            $('a[href="#pills-my-orders"]').on('show.bs.tab', function (e) {
                if ($('#pills-my-orders ul li').length == 0) {
                    loadShopProfileOrders();
                }
            });
            $('#pills-my-orders').on('click', '.shop-profile-more-orders', function(e){
                loadShopProfileOrders();
            })
        </script>
